INSERT INTO product_categories (category_id, description) VALUES (4, 'Auto');
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Cat and Dog Accessories', 1, 'In a world where cats and dogs are loved as family members, it''s important to have the right gear for them, to keep them comfortable and healthy. There are plenty of generic pet supplies out there, but what separates us from the rest is that we offer a personalized, high-quality product that''s made just for your furry friend. We''ve been featured in several internationally recognized pet magazines and currently have thousands of satisfied customers.', 'https://media.istockphoto.com/photos/accessories-for-cat-and-dog-on-blue-background-pet-care-and-training-picture-id1248454290', 1.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Dog Equipment', 1, 'Keeping your dog looking and feeling their best isn''t always easy. That''s why we''ve created a line of high-quality apparel to make sure your pup is well taken care of. All of our products are machine washable and have a simple design, so you''ll never have to worry about getting them dirty.', 'https://media.istockphoto.com/photos/dog-equipment-picture-id154956025', 2.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Carload of Pet Accessories', 1, 'You''re driving your kids to school and they want to stop and get a carload of pet accessories. You''ve just given up on your idea of a quiet morning and now you''re on your way for an expedition to find anything you can get at the store. With this service, we deliver pet accessories right to the door, thanks to our partnerships with major retailers like PetSmart and Van Ness Feeds. Pet owners can order as many items as they need, hassle-free!', 'https://media.istockphoto.com/photos/image-of-different-species-of-goods-in-pet-store-picture-id1212071081', 3.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Leash', 1, 'We all know the frustration of losing our dog in the park. Luckily, you can purchase a dog leash to never lose them again! Suitable for any size or breed, these leashes are available in nine different colors to match your dog''s personality or your style. Made with durable nylon and metal hardware, these leashes will last for years to come.', 'https://media.istockphoto.com/photos/dog-leash-picture-id186725241', 4.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Leash and Toy Kit', 1, 'Would you like to play with your pet and train them, all while they''re wearing a leash? Our leash and toy kit is perfect for interactive pet owners! The kit contains a durable leash with a soft grip, a squeaky toy on the end of the leash, and an adjustable training collar. The adjustable collar can be used to train your pet to stop pulling, for example.', 'https://media.istockphoto.com/photos/pet-bowl-leashes-and-toy-for-dog-pet-accessories-concept-picture-id1166584094', 5.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Sleep Accessory', 1, 'Sleep is an important part of any pet''s health, but for most pets they are too active to rest. Our invention, a sleep accessory for pets, will allow them to sleep in the same room with their owners without disrupting their sleep cycles. Our product is made of high-quality eco-friendly material that is durable and comfortable. Not only does it provide comfort for your pet, but it also helps provide comfort for you when you are trying to sleep at night.', 'https://media.istockphoto.com/photos/an-adult-redhaired-dachshund-is-resting-in-a-white-bed-and-wearing-picture-id1272860452', 6.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Toy Bone', 1, 'The most exciting new bone toy on the market is here! It''s the only toy your pup will ever need. Throw it, chew it, fetch it -- your dog will never get bored with this durable and entertaining toy.', 'https://media.istockphoto.com/photos/dog-toy-picture-id1216392830', 7.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Box of Fun', 1, 'My pet is so adorable that I want to share him/her with everyone. Thatâ€™s why I created a box of fun for my pet. In the box, there are puzzles, toys, and more activities for my pet. It comes in a box that is biodegradable and recyclable so it will be easy to dispose of after the fun is over. The box also comes with a free book full of facts about the animal inside.', 'https://media.istockphoto.com/photos/donation-charity-box-with-animal-food-pet-goods-and-supplies-for-picture-id1290500307', 8.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Laptop', 2, 'Laptops have evolved over the years, and as such, laptops for sale have become a necessity. Whether you''re looking for an inexpensive laptop to get your feet wet or are in the market for a high-end laptop that can handle just about anything you throw at it, you''ll find what you need here. Shop today and save big!', 'https://cdn.pixabay.com/photo/2020/01/26/20/14/computer-4795762__340.jpg', 1.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Glowing Cube', 2, 'The glowing cube is a magic box that can be flipped, rotated, and otherwise manipulated to make the colors appear in any order or pattern. To get started, you need to know what type of LED strips you have. If your LED strip has 4 wires, then it will require a microcontroller with at least 6 pins.', 'https://cdn.pixabay.com/photo/2017/07/10/23/45/cubes-2492010__340.jpg', 2.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('VR Headset', 2, 'VR headset is a personal device used to view virtual reality content. VR headsets are head-mounted goggles with displays that use stereoscopic 3D and 360-degree visual information without being connected to any other devices. It was designed in the early 1960s, and it has been an important part of the video gaming industry ever since.', 'https://cdn.pixabay.com/photo/2018/06/07/16/49/vr-3460451__340.jpg', 3.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Smartphone', 2, 'The smartphone is one of the most revolutionary inventions in modern history. It''s not just that you can use it to play games, take photos or watch videos; it is a mobile device that has changed how we communicate with each other and the world around us. With such capabilities, the smartphone has become an integral part of everyday life for millions of people all over the globe.', 'https://cdn.pixabay.com/photo/2018/01/08/02/34/technology-3068617__480.jpg', 4.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Motherboard', 2, 'The first thing you need to know about motherboard is that it''s not a single component. It''s actually the base of your computer where all the components are attached, such as the CPU and RAM. The motherboard has several important roles: it transmits data between the processor and other devices, lets you add new hardware, and provides power to all your parts.', 'https://cdn.pixabay.com/photo/2013/12/22/15/30/motherboard-232515__480.jpg', 5.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Monitor', 2, 'Monitor shopping is a daunting task. These days, it seems like high resolution monitors are considered standard, while screen sizes and refresh rates seem to be the main differentiators. It can be even more difficult to understand the jargon that manufacturers use to describe their products. This monitor will solve all of your problems', 'https://cdn.pixabay.com/photo/2017/02/12/13/04/lcd-2059995__480.png', 6.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Time Travel Space Ship', 2, 'Time traveler ship. Made in Indonesia, Time Travelers Ship toys with laser and relecteur, made of high quality plush material and electronic parts imported from Germany.', 'https://cdn.pixabay.com/photo/2018/01/31/08/35/space-port-3120607__480.jpg', 7.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Bitcoin', 2, 'Coin is a transferable token,which is denoted by the symbol ``bitcoin.'' It does not take a physical form because it exists in digital space. Its most basic characteristics are fdivisibleeness and fungibility, these are inherit utility.', 'https://cdn.pixabay.com/photo/2018/02/03/02/40/technology-3126814__480.jpg', 8.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Cup of Tea', 3, 'Enhance your mood with a soothing cup of hot tea. With its smooth pear shape, this cup is suitable to brew several different kinds of teas, such as flower and green tea leaves. Its wide mouth makes it especially convenient for serving sweet Eastern-style drinks in elegant surroundings.', 'https://cdn.pixabay.com/photo/2015/07/02/20/37/cup-829527__340.jpg', 1.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Free Weights', 3, 'A great way to tone your body in the comfort of your own home is by adding some strength training. Strength training can come in the form of lifting weights at a gym, but there''s also weight sets available for building strength in the privacy of your own home. The Best Strength Training Sets available.', 'https://cdn.pixabay.com/photo/2017/07/02/19/24/dumbbells-2465478__340.jpg', 2.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Stress Management Kit', 3, 'Stress Management Kit is a time-proven product of its kind with centuries long tradition (any variety). That helps to reduce stress and keep you fit and healthy. It contains an effective blend of herbs and plant extracts in unique, synergistic composition of clinically tested ingredients that results in rapid relief from stress without any side effects or dependency. Stress Management Kit has been developed after detailed clinical studies at some of the best Medical Sciences Universities as well as scientific laboratories. The digestive supplement doesn''t produce any harmful effect', 'https://cdn.pixabay.com/photo/2015/07/30/14/36/hypertension-867855__480.jpg', 3.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Vitamins', 3, 'Formulated to dissolve instantly delivering rapid absorption of nutrition. Support healthy cardiovascular function with 500mg CoQ10 and essential fatty acids (Omega-3, 6 & 9). Supports joint health and bone strength with green-lipped mussel extract. Ester-CÂ® Lemon is a registered trademark of Viverest Ltd, who owns the patents on the coenzyme form synthesized exclusively in New Zealand.', 'https://cdn.pixabay.com/photo/2012/04/10/17/40/vitamins-26622__480.png', 4.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Vaccine', 3, 'This is a vaccination syringe without drugs 100% new medical plastic injection, can be filled with water or fluid from the air no plastic smell do not washing. Details: Length 165mm x Diameter 2.3mm (plastic); Height 220mm x Weight 94g by silica gel technology into colorless and transparent dosage', 'https://cdn.pixabay.com/photo/2021/04/10/00/51/vaccine-6165772__480.jpg', 5.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Dental Care', 3, 'Dental Care kit: You feel yourself healthy because your teeth and mouth is in good shape. A single tooth missing can considerably reduce the efficiency of chewing and smiling; a large number of gaps may cause low self-esteem. Fortunately, the solution is easy to buy an effective tooth whitening system that you can use at home for teeth whitening at home.', 'https://cdn.pixabay.com/photo/2018/03/01/16/43/toothbrush-3191097__480.jpg', 6.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Tablets', 3, 'According to the principle of Traditional Chinese Medicine, Fuzi injection has curative effect on rheumatoid arthritis, intractable edema and pain due to deformation nerve, periodontitis. For treating rheumatoid arthritis and different types of atrophy both local injection therapy and systemic combined treatment are suitable for therapy for rheumatoid arthritis caused by external pathogenic wind, cold injury.', 'https://cdn.pixabay.com/photo/2015/10/22/14/05/tablets-1001224__480.jpg', 7.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Pandemic Management Kit', 3, 'Pandemic mitigation kit by ThinkPandemic is a one-of-a-kind product for this year and beyond. It is actually the first of its kind in the market. The kit contains 30 items, which makes it comprehensive and easy to use. It has latex gloves, bio hazard bags, eye protection equipment, face masks, ear protection supplies and much more (some of these are optional). Each member of your family can easily fit two kits with them into one day bag so they can be comfortable', 'https://cdn.pixabay.com/photo/2020/11/01/17/53/coronavirus-5704493__480.png', 8.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Sleek Car from the Future', 4, 'This fabulous 3D model was created for researches in user-interaction and navigation. At first sight it seems to be a realistic, but non-functional prototype of a sports car from the future. In the tool scene invoked, you are able to change some design details (e.g.. all colors, seats) using simple sliders.', 'https://cdn.pixabay.com/photo/2012/05/29/00/43/car-49278__340.jpg', 1.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Sleek Car from the Past', 4, 'The two-door model sports a rakish look, with the  sloping shape of its design drawing clear parallels to the 1962 Grand Sport Corvette racer. The erect cockpit provides a driver-focused environment, mounting instruments in clean clusters bordered by quad projector lenses and offering plenty of leg, elbow, shoulder and headroom. Its ultramodern interpretation of the Chevrolet Bel Air sport coupe will make sleek drivers of today feel they re time traveling in the all-electric fleets', 'https://cdn.pixabay.com/photo/2015/05/28/23/12/auto-788747__340.jpg', 2.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Vintage Car', 4, 'Great product to your collection of automobiles. Features:Â· Includes detailed tools and instruments- exact down to the tiny wrenches and screwdrivers in the back storage area of the car, shop manual on a clip board, underwork light still works, movable stool leg chases everything around. Very hard to get most cars like this these days at this', 'https://cdn.pixabay.com/photo/2016/11/29/09/32/auto-1868726__340.jpg', 3.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Cargo Van', 4, 'The 98-04 were a much vaunted era of styling because the Ford trucks were becoming more modern each year, yet looked classic with the same grille from 1998-2004! This van is perfectly restored and made to be used as an everyday driver. Has a powerful optional engine with factory dual exhausts. The condition of this van will amaze even savvy car enthusiasts ! WOW !!!', 'https://cdn.pixabay.com/photo/2012/04/25/00/52/minivan-41476__480.png', 4.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Taxi Cab', 4, 'Hi there Nice to meet you! This is our product page for rent a car service. We offer special rate,we provide car fleet from 3-6 seaters in budget rates. If you are willing to start a taxi cab business in your home town we might be worth the trust of starting this business from the scratch and we can rise you with all information about these services and if needed we can supply some cars for test runs to hunt for customers.', 'https://cdn.pixabay.com/photo/2014/07/13/19/45/edsel-ranger-392745__480.jpg', 5.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Rickshaw', 4, 'Rickshaw is a new concept of car-sharing combined the social side-project likes Couchsurfing.com and Airbnb.com. We enables peer to peer car sharing & rent same way you do couchsurfing by helping you connect quickly with people around your neighborhood or at other locations. You can choose your ride time up to 1 hour and just pay what you think it''s worth, as little as $5 an hour or even less (1/3rd cheaper than a taxi).', 'https://cdn.pixabay.com/photo/2017/03/20/08/05/rickshaw-2158447__480.jpg', 6.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Racecar', 4, 'Our little boy loves his race car, he attacks the floor and the chairs with it. We were please upon arrival to find new batteries so he ran on the spot within 12hours of buying! So much relief', 'https://cdn.pixabay.com/photo/2016/03/26/22/32/fast-1281628__480.jpg', 7.00);
INSERT INTO products(name, category_id, description, image_url, price) VALUES ('Pick-up Truck', 4, 'This truck can be the children''s early education toy and simulation system to prepare for the future, students play this truck in the learn about long time driving skills ability.', 'https://cdn.pixabay.com/photo/2016/04/01/12/11/pickup-truck-1300585__340.png', 8.00);
